Scripts.Onload:
    	  self:SetOwner(WorldFrame, "ANCHOR_NONE");
    	  self:SetScale(.75);
				self:SetBackdropBorderColor(TOOLTIP_DEFAULT_COLOR.r, TOOLTIP_DEFAULT_COLOR.g, TOOLTIP_DEFAULT_COLOR.b);
				self:SetBackdropColor(TOOLTIP_DEFAULT_BACKGROUND_COLOR.r, TOOLTIP_DEFAULT_BACKGROUND_COLOR.g, TOOLTIP_DEFAULT_BACKGROUND_COLOR.b);

Scripts.OnClick:
KarniCrap_Set_InventorySelected(self:GetParent():GetID());
KarniCrap_SelectInventoryItem();
KarniCrap_ConfirmDestroySelected();

KarniCrap_Set_BlacklistSelected(self:GetID());
KarniCrap_SelectBlacklistItem();
PlaySound("igMainMenuOptionCheckBoxOn");

 	KarniCrap_Set_WhitelistSelected(self:GetID());
	KarniCrap_SelectWhitelistItem();
	PlaySound("igMainMenuOptionCheckBoxOn");

KarniCrap_Set_InventorySelected(self:GetID());
KarniCrap_SelectInventoryItem();
PlaySound("igMainMenuOptionCheckBoxOn");

KarniCrap_BtnDestroyItem:Disable();
KarniCrap_BtnBlacklistRemove:Disable();
KarniCrap_BtnWhitelistRemove:Disable();
KarniCrap_InitializeBlacklist();
KarniCrap_InitializeWhitelist();
StaticPopup_Hide("KARNICRAP_CONFIRMDESTROYSELECTED")
StaticPopup_Hide("KARNICRAP_CONFIRMDESTROYALLCRAP")
KarniCrap_ToggleFrame();

KarniCrap_BtnDestroyItem:Disable();
KarniCrap_BtnBlacklistRemove:Disable();
KarniCrap_BtnWhitelistRemove:Disable();
KarniCrap_InitializeBlacklist();
KarniCrap_InitializeWhitelist();
StaticPopup_Hide("KARNICRAP_CONFIRMDESTROYSELECTED")
StaticPopup_Hide("KARNICRAP_CONFIRMDESTROYALLCRAP")
KarniCrap_ToggleFrame();

if (self:GetChecked()) then 
	KarniCrapConfig.UseStackValue = true;
else 
	KarniCrapConfig.UseStackValue = false;
end

if (self:GetChecked()) then 
	KarniCrapConfig.Echo = true;
else 
	KarniCrapConfig.Echo = false;
end

 
							if self:GetChecked() then
								KarniCrapConfig.DestroySlots = true;
								KarniCrap_EBDestroySlotsNum:Show();
								KarniCrap_EBDestroyNumDisabled:Hide();
							else
								KarniCrapConfig.DestroySlots = false;
								KarniCrap_EBDestroySlotsNum:Hide();
								KarniCrap_EBDestroyNumDisabled:Show();
								
							end

Scripts.OnEnter:
KarniCrapTooltip:SetOwner(self, "ANCHOR_TOPRIGHT");
KarniCrapTooltip:SetText("|cffffffffAlways loot|r");
KarniCrapTooltip:AddLine("Check this to loot cloth regardless|nof |cffffffff[common]|r item filter settings");
KarniCrapTooltip:Show();

KarniCrapTooltip:SetOwner(self, "ANCHOR_TOPRIGHT");
KarniCrapTooltip:SetText("|cffffffffNever loot|r");
KarniCrapTooltip:AddLine("Check this to filter or destroy cloth|nregardless of |cffffffff[common]|r item filter settings");
KarniCrapTooltip:Show();

Scripts.OnClick:
if (self:GetChecked()) then	
	KarniCrapConfig.Cloth_Linen = true;							
	KarniCrapConfig.Cloth_Linen_Never = false;
	_G["KarniCrap_Cloth_CBLinen_Never"]:SetChecked(false);
else 
	KarniCrapConfig.Cloth_Linen = false;	
end

if (self:GetChecked()) then	
	KarniCrapConfig.Cloth_Linen_Never = true;
	KarniCrapConfig.Cloth_Linen = false;
	_G["KarniCrap_Cloth_CBLinen"]:SetChecked(false);
