if KarniCrapConfig.InvSort == "Current Value" then
	self:SetText("Stack Value");
	KarniCrapConfig.InvSort = "Stack Value";
	KarniCrap_InventoryScrollbarUpdate();
elseif KarniCrapConfig.InvSort == "Stack Value" then
	self:SetText("Item Value");
	KarniCrapConfig.InvSort = "Item Value";
	KarniCrap_InventoryScrollbarUpdate();
else
	self:SetText("Current Value");
	KarniCrapConfig.InvSort = "Current Value";
	KarniCrap_InventoryScrollbarUpdate();
end

if (self:GetChecked()) then KarniCrapConfig.HideQuestItems = true;
else KarniCrapConfig.HideQuestItems = false;
end
KarniCrap_InventoryList();

if (self:GetChecked()) then KarniCrapConfig.OpenAtMerchant = true;
else KarniCrapConfig.OpenAtMerchant = false;
end

Scripts.OnEnter:
 
				if _G["KarniCrap_Tab2"]:IsVisible() then
					if CursorHasItem() then 
						_G["KarniCrap_Blacklist"]:EnableMouse("true"); 
						_G["KarniCrap_Whitelist"]:EnableMouse("true"); 
					else
						_G["KarniCrap_Blacklist"]:EnableMouse("false"); 
						_G["KarniCrap_Whitelist"]:EnableMouse("false");
					end
				end
